<div class="relative">
    @if(!$nested)
        <div class="flex items-center justify-end gap-2 text-xs md:text-base">
            <div
                    class="flex flex-row-reverse flex-wrap items-center justify-start flex-1 gap-2 px-4 py-2 bg-dark rounded-3xl dark:bg-neutral">
                <a href="{{ route('admin.user.index') }}"
                   wire:navigate
                   class="cursor-pointer">المستخدمين</a>

            </div>
        </div>
    @endif


    <div class="mt-10">
        <div class="grid w-full gap-4 mt-6 lg:grid-cols-12">
            {{--            Right --}}
            <div class="p-3 rounded-lg lg:col-span-4 bg-base-300">
                {{--                First Line --}}
                <div class="grid grid-cols-3  gap-2 h-24">
                    <div class="col-span-1 flex items-center justify-center">
                        <img src="{{ Storage::url($user->avatar) }}"
                             class="w-24 h-24 rounded border border-base-200 "
                             alt="{{ $user->first_name . ' avatar' }}"/>
                    </div>
                    <div class="col-span-2 flex flex-col items-start justify-between">
                        <h2 class="font-bold">{{ $user->first_name . ' ' .
                        $user->middle_name .' ' . $user->last_name }}</h2>
                        <p> كود الطالب <span
                                    class="text-primary font-semibold">{{$user->id}}</span>
                        </p>
                        <p class="text-primary font-semibold">  {{ $user->email  }}</p>
                    </div>
                </div>

                {{--                Second Line--}}
